// 初始化函数
function initSmiley() {
	// 删除默认tab
	//removeTab(1);		// 删除某个tab
	//removeTab();		// 全部删除

	// 添加tab
	addTab("金馆长", konata_collection);
	addTab("幸运星", lucky_stars_collection);
	addTab("彼尔德", lucky_star_collection);
	addTab("洋葱头", onion_collection);
	addTab("日和", ri_collection);
	addTab("罗小黑", hei_collection);
	addTab("鱼兔妹", y_collection);
	addTab("自定义", getFavorites(), true);	// true表示这是自定义

	// 替换tab
	//replaceTab(0, "常用", usual_collection);	// 替换某个tab，比addTab多一个index参数

	// 切换到上次的tab
	FaceHandler.tabExist[tab_last=0]=0;
	unsafeWindow.switchTab(localStorage.getItem('ge_smileytab')||tm.firstElementChild.id.substr(4));
}

var FaceHandler=unsafeWindow.FaceHandler,config=unsafeWindow.config,faceBox=unsafeWindow.faceBox,$G=unsafeWindow.$G,
	tab_last=10,tc=$G("tabContent"),tm=$G("tabMenu"),favtab=null,favemotions;
tm.style.overflow = "auto";
tm.style.height = "300px";
function removeTab(index) {
	if(index==null) {
		tm.innerHTML=tc.innerHTML='';
		config={};
	} else {
		tm.removeChild($G('tab_'+index));
		tc.removeChild($G('tab'+index));
		delete config['tab'+index];
	}
}
function addTab(name, col, fav) {
	tab_last++;
	var tabid = "tab" + tab_last;
	initTab(tabid,col,fav);
	var d = document.createElement("div");
	d.id = tabid;
	tc.appendChild(d);
	d = document.createElement("div");
	d.id = "tab_" + tab_last;
	d.setAttribute("onclick", "switchTab(" + tab_last + ")");
	d.innerHTML = "<u>" + name + "</u>";
	tm.appendChild(d);
	return tabid;
}
function replaceTab(id, name, col, fav) {
	var tabid='tab'+id;
	initTab(tabid,col,fav);
	delete FaceHandler.imageFolders[tabid];
	if(name) $G('tab_'+id).innerHTML='<u>'+name+'</u>';
	return tabid;
}
function initTab(tabid,col,fav) {
	config[tabid] = [ 1, 0, tab_last ];
	FaceHandler.tabExist.push(0);
	FaceHandler.imageWidth[tabid] = 35;
	FaceHandler.imageCols[tabid] = 7;
	FaceHandler.imageColWidth[tabid] = 3;
	FaceHandler.imageCss[tabid] = "bb";
	FaceHandler.imageCssOffset[tabid] = 35;
	faceBox[tabid] = col;
	if (fav) favtab = tabid;
}
var _createTab = unsafeWindow.createTab;
unsafeWindow.createTab = function(tabName) {
	if (FaceHandler.imageFolders[tabName]) return _createTab(tabName);
	var tab = $G(tabName);
	var positionLine = FaceHandler.imageCols[tabName] / 2;
	var iWidth = iHeight = FaceHandler.imageWidth[tabName];
	var iColWidth = FaceHandler.imageColWidth[tabName];
	var textHTML = [ '<table cellpadding="1" cellspacing="1" align="center" style="border-collapse:collapse;" border="1" bordercolor="#B8B3FF" width="100%" height="100%">' ];
	var imgNum = faceBox[tabName].length;
	if (imgNum < 56) imgNum = 56;
	for (var i = 0; i < imgNum; ) {
		textHTML.push("<tr>");
		for (var j = 0, imgColNum = FaceHandler.imageCols[tabName]; j < imgColNum; j++, i++) {
			var faceImage = faceBox[tabName][i];
			if (faceImage) {
				var posflag = j < positionLine ? 0 : 1;
				textHTML.push('<td border="1" width="' + iColWidth + '%" height=35px style="border-collapse:collapse;" align="center"  bgcolor="#FFFFFF" onclick="InsertSmiley(\'' + faceImage.replace(/'/g, "\\'") + "','')\" onmouseover=\"over(this,'" + faceImage + "','" + posflag + '\')" onmouseout="out(this)">');
				textHTML.push('<img src="' + faceImage + '" width=35px height=35px>');
			} else textHTML.push('<td width="' + iColWidth + '%" height=35px bgcolor="#FFFFFF">');
			textHTML.push("</td>");
		}
		textHTML.push("</tr>");
	}
	textHTML.push("</table>");
	textHTML = textHTML.join("");
	tab.innerHTML = textHTML;
	if (tabName == favtab) {	// 自定义表情tab拖拽支持
		tab.children[0].addEventListener("drop", function(e) {	// 添加自定义表情
			e.preventDefault();
			var url = e.dataTransfer.getData("url");
			if (!/\.(jpg|gif|png)(\?|$)/i.test(url)) return;
			if (favemotions.indexOf(url) >= 0) return;
			favemotions.push(url);
			saveFavorites();
			unsafeWindow.createTab(favtab);
		}, false);
		tab.children[0].addEventListener("dragover", function(e) {
			e.preventDefault();
		}, false);
		tm.addEventListener("drop", function(e) {		// 删除自定义表情
			e.preventDefault();
			var url = e.dataTransfer.getData("url");
			if (favemotions.indexOf(url) < 0) return;
			favemotions.splice(favemotions.indexOf(url), 1);
			saveFavorites();
			unsafeWindow.createTab(favtab);
		$G("tabIconReview").className="";
		}, false);
		tm.addEventListener("dragover", function(e) {
			e.preventDefault();
		}, false);
	}
};
unsafeWindow.switchTab=function(index){
	//不存在,则现生成
	if(config['tab'+index][0]==0) return;
	if(FaceHandler.tabExist[index]==0){
		FaceHandler.tabExist[index]=1;
		unsafeWindow.createTab('tab'+index);
	}
	var t;
	if(t=$G('tab_'+tab_last)) {
		t.className="menuDefault";
		$G('tab'+tab_last).style.display="none";
	}
	tab_last=index;
	//显示对应呈现元素
	$G("tab_"+index).className="menuFocus";
	$G("tab"+index).style.display="block";
	localStorage.setItem("ge_smileytab",index);
}
function getFavorites() {return favemotions=JSON.parse(localStorage.getItem("ge_emotion")||'[]');}
function saveFavorites() {localStorage.setItem("ge_emotion", JSON.stringify(favemotions));}
